#{tag.tagId}
)AND (Post.InsertUserName = User.Username)) OR ((User.IsSuperUser = 1) AND (Post.InsertUserName = User.Username))) AND (DateInserted >= #{begin}) AND (DateInserted <= #{end}) AND (isActive = 1) AND (IsBroadcast = 1) ORDER BY DateInserted DESC LIMIT #{count}

