mapper.update:
UPDATE Follow SET
	isActive = "0",
	DateUpdated = #{dateUpdated}
WHERE Username = #{username} AND FollowingUser = #{followingUser}

UPDATE NewFollowerCount SET
	Count = Count + 1
WHERE Username = #{username}

UPDATE NewFollowerCount SET
	Count = 0
WHERE Username = #{username}

mapper.select:
SELECT count(*) 
FROM Follow
WHERE Username = #{username} AND FollowingUser = #{followingUser}
