mapper.update:
UPDATE Post SET
	isActive = "0"
WHERE POSTID = #{postID} AND InsertUserName = #{insertUserName}

mapper.select:
SELECT PostID, Type, InsertUserName, body, Tags, CountComments, CountBookmarks, CountViews, Closed, IsBroadcast, DateInserted, DateUpdated, isActive FROM Post WHERE PostID IN( SELECT PostID FROM PostTag WHERE PostTag.Tagname IN
#{tagname}
)
