mapper.update:
UPDATE Post SET
	isActive = "0"
WHERE POSTID = #{postID} AND InsertUserName = #{insertUsername}

mapper.select:
SELECT DISTINCT PostID, Type, InsertUserName, body, Tags, CountComments, CountBookmarks, CountViews, Closed, IsBroadcast, DateInserted, DateUpdated, isActive, image FROM Post, User WHERE PostID IN( SELECT PostID FROM PostTag WHERE PostTag.TagID IN
#{tag.tagId}
)AND (Post.InsertUserName = User.Username) AND (DateInserted >= #{begin}) AND (DateInserted <= #{end}) AND (isActive = 1) AND (IsBroadcast = 1) ORDER BY DateInserted DESC
