#{tag.tagId}
)AND (post.InsertUserName = user.Username))) AND (DateInserted >= #{begin}) AND (DateInserted <= #{end}) AND (isActive = 1) AND (IsBroadcast = 1) OR ((user.IsSuperUser = 1) AND (post.InsertUserName = user.Username) AND (DateInserted >= #{begin}) AND (DateInserted <= #{end}) ) ORDER BY DateInserted DESC LIMIT #{count}

